	var JoystickL = document.getElementById('joystickLeft');
	JoystickL.ontouchstart = function(event) { moveLeft = true; }
	JoystickL.ontouchend = function(event) { moveLeft = false; }
	JoystickL.ontouchmove = function(event) { moveLeft = false; }


	// Update game objects
	var update = function (modifier) {
		if(!gameOver){
			if (38 in keysDown || 87 in keysDown ) { // Player holding up
				hero.y -= hero.speed * modifier;
			}
			if (40 in keysDown || 83 in keysDown ) { // Player holding down
				hero.y += hero.speed * modifier;
			}
			if (37 in keysDown || 65 in keysDown ) { // Player holding left
				hero.x -= hero.speed * modifier;
			}
			if (39 in keysDown || 68 in keysDown ) { // Player holding right
				hero.x += hero.speed * modifier;
			}

			//Joystick
			if(moveUp){ hero.y -= hero.speed * modifier; }
			if(moveDown){ hero.y += hero.speed * modifier; }
			if(moveLeft){ hero.x -= hero.speed * modifier; }
			if(moveRight){ hero.x += hero.speed * modifier; }
		}

		//move Monster towards the player
		if(hero.x + (Tilesize/2) > monster.x && HealthNum >=1){
			monster.x += monster.speed * modifier;
		}
		if(hero.x - (Tilesize/2) < monster.x && HealthNum >=1){
			monster.x -= monster.speed * modifier;
		}
		if(hero.y + (Tilesize/2) > monster.y && HealthNum >=1){
			monster.y += monster.speed * modifier;
		}
		if(hero.y - (Tilesize/2) < monster.y && HealthNum >=1){
			monster.y -= monster.speed * modifier;
		}

		// Are they touching? (MONSTER / HERO)
		if (
			hero.x <= (monster.x + (Tilesize-(Tilesize/4)) )
			&& monster.x <= (hero.x + (Tilesize-(Tilesize/4)) )
			&& hero.y <= (monster.y + (Tilesize-(Tilesize/4)) )
			&& monster.y <= (hero.y + (Tilesize-(Tilesize/4)) )
			) {
			subtractHealth();
		reset();
	}

		// Are they touching? (TREASURE / HERO)
		if (
			hero.x <= (treasure.x + (Tilesize-(Tilesize/4)) )
			&& treasure.x <= (hero.x + (Tilesize-(Tilesize/4)) )
			&& hero.y <= (treasure.y + (Tilesize-(Tilesize/4)) )
			&& treasure.y <= (hero.y + (Tilesize-(Tilesize/4)) )
			) {
			++treasureCollected;
		reset();
		monster.speed = monster.speed + 5;
	}

		//make player move to opposite side of screen instead of just disssapearing
		if(hero.x > window.innerWidth - (Tilesize/2)){
			hero.x = -(Tilesize/2);
		}
		if(hero.x < -(Tilesize/2)){
			hero.x = window.innerWidth - (Tilesize/2);
		}
		if(hero.y > window.innerHeight - (Tilesize/2)){
			hero.y = -(Tilesize/2);
		}
		if(hero.y < -(Tilesize/2)){
			hero.y = window.innerHeight - (Tilesize/2);
		}

		//keep the ground tiles fullscreen size
		window.addEventListener('resize', resizeCanvas, false);

		function resizeCanvas() {
			GameBox_Width = window.innerWidth;
			GameBox_Height = window.innerHeight;
			canvas.width = GameBox_Width;
			canvas.height = GameBox_Height;
		}



	};

